import React, { useState, useEffect } from "react";
import { Nav, Navbar } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import './NavBar.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFacebook, faInstagram, faLinkedin, faGithub } from '@fortawesome/free-brands-svg-icons'




function NavBar() {
    const [isScrolled, setIsScrolled] = useState(false);

    useEffect(() => {
        function handleScroll() {
            if (window.scrollY > 0) {
                setIsScrolled(true);
            } else {
                setIsScrolled(false);
            }
        }
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <Navbar

            expand="sm"
            className={`navbar-scrolled ${isScrolled ? "fixed-top" : ""}`}
        >
            <Navbar.Brand href="/">Home</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav>
                    <Nav.Link href="/about">About</Nav.Link>
                    <Nav.Link href="/projects">Projects</Nav.Link>
                    <Nav.Link href="/contact">Contact</Nav.Link>
                    <Nav.Link href="https://www.facebook.com/"><FontAwesomeIcon icon={faFacebook} size="xl" /></Nav.Link>
                    <Nav.Link href="https://www.instagram.com/"><FontAwesomeIcon icon={faInstagram} size="xl" /></Nav.Link>
                    <Nav.Link href="https://www.linkedin.com/"><FontAwesomeIcon icon={faLinkedin} size="xl" /></Nav.Link>
                    <Nav.Link href="https://www.github.com/"><FontAwesomeIcon icon={faGithub} size="xl" /></Nav.Link>

                </Nav>
            </Navbar.Collapse>
        </Navbar>
    );
};


export default NavBar;
